CREATE OR REPLACE FUNCTION public.get_affiliate_metrics(p_affiliate_id uuid)
 RETURNS json
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
    total_clients INT;
    active_clients INT;
    total_revenue NUMERIC;
BEGIN
    SELECT COUNT(*) INTO total_clients FROM companies WHERE affiliate_id = p_affiliate_id;
    SELECT COUNT(*) INTO active_clients FROM companies WHERE affiliate_id = p_affiliate_id AND status = 'active';
    SELECT COALESCE(SUM(p.price), 0) INTO total_revenue 
    FROM companies c
    JOIN plans p ON c.plan_id = p.id
    WHERE c.affiliate_id = p_affiliate_id AND c.status = 'active';

    RETURN json_build_object(
        'total_clients', total_clients,
        'active_clients', active_clients,
        'total_revenue', total_revenue
    );
END;
$function$

CREATE OR REPLACE FUNCTION public.get_super_admin_metrics()
 RETURNS json
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
    total_affiliates INT;
    total_clients INT;
    total_revenue NUMERIC;
BEGIN
    SELECT COUNT(*) INTO total_affiliates FROM affiliates;
    SELECT COUNT(*) INTO total_clients FROM companies;
    SELECT COALESCE(SUM(p.price), 0) INTO total_revenue 
    FROM companies c
    JOIN plans p ON c.plan_id = p.id
    WHERE c.status = 'active';

    RETURN json_build_object(
        'total_affiliates', total_affiliates,
        'total_clients', total_clients,
        'total_revenue', total_revenue
    );
END;
$function$

CREATE OR REPLACE FUNCTION public.login(email_param text, password_param text)
 RETURNS json
 LANGUAGE plpgsql
AS $function$
DECLARE
    user_record public.users;
    result json;
BEGIN
    SELECT * INTO user_record
    FROM public.users
    WHERE email = email_param;

    IF NOT FOUND THEN
        RETURN json_build_object('status', 404, 'message', 'Usuário não encontrado');
    END IF;

    -- A função crypt() compara a senha enviada com o hash salvo
    IF user_record.password_hash = crypt(password_param, user_record.password_hash) THEN
        result := json_build_object(
            'status', 200,
            'message', 'Login bem-sucedido',
            'payload', json_build_object(
                'userId', user_record.id,
                'companyId', user_record.company_id,
                'role', user_record.role,
                'fullName', user_record.full_name
            )
        );
        RETURN result;
    ELSE
        RETURN json_build_object('status', 401, 'message', 'Senha inválida');
    END IF;
END;
$function$
